  <span class="token punctuation">}</span>
<span class="token punctuation">}</span>

<span class="token comment">// 设置cookie</span>
CookieUtil<span class="token punctuation">.</span><span class="token function">set</span><span class="token punctuation">(</span><span class="token string">"name"</span><span class="token punctuation">,</span> <span class="token string">"Nicholas"</span><span class="token punctuation">)</span><span class="token punctuation">;</span>
