<span class="token function">alert</span><span class="token punctuation">(</span>CookieUtil<span class="token punctuation">.</span><span class="token function">get</span><span class="token punctuation">(</span><span class="token string">"book"</span><span class="token punctuation">)</span><span class="token punctuation">)</span><span class="token punctuation">;</span> <span class="token comment">// "Professional JavaScript"</span>

<span class="token comment">// 删除cookie</span>
CookieUtil<span class="token punctuation">.</span><span class="token function">unset</span><span class="token punctuation">(</span><span class="token string">"name"</span><span class="token punctuation">)</span><span class="token punctuation">;</span>
CookieUtil<span class="token punctuation">.</span><span class="token function">unset</span><span class="token punctuation">(</span><span class="token string">"book"</span><span class="token punctuation">)</span><span class="token punctuation">;</span>

<span class="token comment">// 设置有路径、域和过期时间的cookie</span>
CookieUtil<span class="token punctuation">.</span><span class="token function">set</span><span class="token punctuation">(</span>
